// src/components/Home.js
import React, { useState, useEffect } from 'react';

const Home = () => {
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch weather data from the backend API (serverless function)
    fetch('https://guileless-sable-7aa0f2.netlify.app/.netlify/functions/api/weather')
      .then((response) => response.json())
      .then((data) => {
        setWeather(data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error.message);
        setLoading(false);
      });
  }, []);

  return (
    <div className="text-center my-5">
      <h1 className="display-4">
        Hi, I'm Zhiying! <br /> Welcome to My Portfolio
      </h1>
      <br />
      <p className="lead">
        Cybersecurity Research | Full-Stack Developer | Coop at the NSERC CREATE Cybersecurity Program
      </p>
      <br />
      <img
        src="profile.jpeg"
        alt="Zhiying Wu, a third-year computer science student"
        className="img-fluid rounded-circle"
        width="500"
      />

      {/* Weather Information */}
      <div className="weather-info mt-5">
        {loading && <p>Loading weather...</p>}
        {error && <p>Error: {error}</p>}
        {weather && (
          <div>
            <h3>Weather in {weather.city}, {weather.country}</h3>
            <p>{weather.temperature.current}°C (Feels like: {weather.temperature.feels_like}°C)</p>
            <p><strong>Wind Speed:</strong> {weather.wind.speed} m/s</p>
            <p><strong>Humidity:</strong> {weather.humidity}%</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
