import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './components/Home';
import About from './components/About';
import Projects from './components/Projects';
import NotFound from './components/NotFound';
import Skills from './components/Skills';
import ThemeSwitcher from './components/ThemeSwitcher';

import './App.css';

function App() {
  // Set up state for weather data
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch weather data when the app loads
  useEffect(() => {
    const apiKey = process.env.REACT_APP_SEC_API; // Access the environment variable
    const fetchWeatherData = async () => {
      try {
        const response = await fetch(
          `https://api.openweathermap.org/data/2.5/weather?q=halifax,ca&units=metric&appid=${apiKey}`
        );
        const data = await response.json();
        if (response.ok) {
          setWeatherData(data); // Set the weather data to state
        } else {
          throw new Error(data.message || 'Something went wrong');
        }
      } catch (err) {
        setError(err.message); // Set error state if there's an error
      } finally {
        setLoading(false); // Once the request completes, stop the loading state
      }
    };

    fetchWeatherData();
  }, []);

  return (
    <Router>
      <Header />
      <ThemeSwitcher />

      {/* If there's an error or still loading, show loading/error message */}
      {loading ? (
        <div>Loading weather data...</div>
      ) : error ? (
        <div>Error: {error}</div>
      ) : (
        <div>
          <h2>Weather Information</h2>
          <p>City: {weatherData.name}, {weatherData.sys.country}</p>
          <p>Temperature: {weatherData.main.temp}Â°C</p>
          <p>Humidity: {weatherData.main.humidity}%</p>
          <p>Wind Speed: {weatherData.wind.speed} m/s</p>
        </div>
      )}

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/projects" element={<Projects />} />
        <Route path="/skills" element={<Skills />} />
        <Route path="*" element={<NotFound />} />
      </Routes>

      <Footer />
    </Router>
  );
}

export default App;
