import React, { useState } from 'react';

// Sample skill data with categories
const skillsData = [
  { id: 1, skill: 'React', category: 'Frontend' },
  { id: 2, skill: 'Node.js', category: 'Backend' },
  { id: 3, skill: 'CSS', category: 'Frontend' },
  { id: 4, skill: 'Express.js', category: 'Backend' },
  { id: 5, skill: 'MongoDB', category: 'Database' },
  { id: 6, skill: 'JavaScript', category: 'Frontend' },
  { id: 7, skill: 'Python', category: 'Backend' },
];

const Skills = () => {
  // State for search input and selected categories
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategories, setSelectedCategories] = useState({
    Frontend: false,
    Backend: false,
    Database: false,
  });

  // Update search term dynamically as the user types
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // Toggle category selection (checkboxes)
  const handleCategoryChange = (e) => {
    const { name, checked } = e.target;
    setSelectedCategories((prev) => ({
      ...prev,
      [name]: checked,
    }));
  };

  // Filter the skills based on search term and selected categories
  const filteredSkills = skillsData.filter((skill) => {
    // Matches search term in skill name (case-insensitive)
    const matchesSearch =
      skill.skill.toLowerCase().includes(searchTerm.toLowerCase());

    // Matches search term for category (case-insensitive)
    const matchesCategory = Object.keys(selectedCategories).some(
      (category) =>
        selectedCategories[category] &&
        category.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // If the category matches or no category is selected, return the skill
    const matchesSelectedCategory =
      selectedCategories[skill.category] ||
      Object.values(selectedCategories).every((value) => !value);

    return (matchesSearch || matchesCategory) && matchesSelectedCategory;
  });

  return (
    <div>
      <h2>Skills</h2>

      {/* Search bar */}
      <input
        type="text"
        placeholder="Search for a skill or category"
        value={searchTerm}
        onChange={handleSearchChange}
        style={{
          padding: '10px',
          margin: '10px 0',
          width: '100%',
          maxWidth: '300px',
          borderRadius: '5px',
          border: '1px solid #ccc',
        }}
      />

      {/* Category Filters (checkboxes) */}
      <div style={{ margin: '10px 0' }}>
        <label>
          <input
            type="checkbox"
            name="Frontend"
            checked={selectedCategories.Frontend}
            onChange={handleCategoryChange}
          />
          Frontend
        </label>
        <label style={{ marginLeft: '15px' }}>
          <input
            type="checkbox"
            name="Backend"
            checked={selectedCategories.Backend}
            onChange={handleCategoryChange}
          />
          Backend
        </label>
        <label style={{ marginLeft: '15px' }}>
          <input
            type="checkbox"
            name="Database"
            checked={selectedCategories.Database}
            onChange={handleCategoryChange}
          />
          Database
        </label>
      </div>

      {/* Displaying filtered skills */}
      <div>
        {filteredSkills.length > 0 ? (
          filteredSkills.map((skill) => (
            <div key={skill.id} style={{ marginBottom: '10px' }}>
              <strong>{skill.skill}</strong> ({skill.category})
            </div>
          ))
        ) : (
          <p>No skills found matching your search or selected categories.</p>
        )}
      </div>
    </div>
  );
};

export default Skills;
